import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './paginaInicial.css';

export default function PaginaInicial() {
  const navigate = useNavigate();
  const [livros, setLivros] = useState([]);
  const [filtro, setFiltro] = useState('');
  const [filtros, setFiltros] = useState([]);
  const [filtroSelecionado, setFiltroSelecionado] = useState('');

  // Buscar livros
  useEffect(() => {
    fetch('http://localhost:5000/livros')
      .then(response => response.json())
      .then(data => setLivros(data))
      .catch(error => console.error('Erro ao buscar livros:', error));
  }, []);

  // Buscar filtros
  useEffect(() => {
    fetch('http://localhost:5000/filtros')
      .then(response => response.json())
      .then(data => setFiltros(data))
      .catch(error => console.error('Erro ao buscar filtros:', error));
  }, []);

  // Atualizar campo de busca
  const handleFiltroChange = (event) => {
    setFiltro(event.target.value);
  };

  // Aplicar filtro de categoria
  const handleFiltroSelecionado = (filtro) => {
    setFiltroSelecionado(filtro);
  };

  // Filtrar os livros
  const livrosFiltrados = livros.filter((livro) => {
    const titulo = livro.titulo?.toLowerCase() || '';
    const categoria = livro.categoria?.toLowerCase() || '';
    return (
      titulo.includes(filtro.toLowerCase()) &&
      (!filtroSelecionado || categoria === filtroSelecionado.toLowerCase())
    );
  });

  return (
    <div className="pagina-inicial">
      {/* Topo com barra de busca e botões */}
      <div className="topo">
        <input
          type="text"
          placeholder="Buscar por título"
          value={filtro}
          onChange={handleFiltroChange}
        />
        <div className="botoes-topo">
          <button onClick={() => navigate('/adicionar-produto')}>Postar Livro</button>
          <button onClick={() => navigate('/perfil')}>Perfil</button>
        </div>
      </div>

      {/* Filtros por categoria */}
      <div className="filtros">
        <button onClick={() => setFiltroSelecionado('')}>Todos</button>
        {filtros.map((filtro, index) => (
          <button key={index} onClick={() => handleFiltroSelecionado(filtro)}>
            {filtro}
          </button>
        ))}
      </div>

      {/* Lista de livros */}
      <div className="livros">
        {livrosFiltrados.length > 0 ? (
          livrosFiltrados.map((livro) => (
            <div
              key={livro.id}
              className="livro"
              onClick={() => navigate(`/livros/${livro.id}`)}
            >
              <img src={livro.imagem} alt={livro.titulo} />
              <h3>{livro.titulo}</h3>
              <p>Preço: R$ {livro.valor.toFixed(2)}</p>
            </div>
          ))
        ) : (
          <p>Nenhum livro encontrado.</p>
        )}
      </div>
    </div>
  );
}
